func TestFastpath(t *testing.T) {
	for _, tc := range cases {
		t.Run(tc.Name, func(t *testing.T) {
			// bytes.Reader does not return io.EOF immediately
			r := bytes.NewReader(tc.Payload)
			hostname, buf, err := internal.ReadHostnameFastPath(r)
			if err != tc.Err {
				t.Logf("err: %#v", err)
				t.Fail()
			}
			if hostname != tc.Hostname {
				t.Logf("hostname: %q", hostname)
				t.Fail()
			}
			if !bytes.Equal(buf, tc.Payload) {
				t.Logf("buf:\n%s", hex.Dump(buf))
				t.Fail()
			}
		})
	}
}
